const { classes: Cc, interfaces: Ci, utils: Cu } = Components;

Cu.import("resource://gre/modules/Home.jsm");
Cu.import("resource://gre/modules/HomeProvider.jsm");
Cu.import("resource://gre/modules/Services.jsm");
Cu.import("resource://gre/modules/Task.jsm");

function log(msg) {
  Services.console.logStringMessage("*** HOME DEMO: " + msg);
}

const LIST_PANEL_ID = "com.margaretleibovic.demo.list";
const GRID_PANEL_ID = "com.margaretleibovic.demo.grid";
const DATASET_ID = "com.margaretleibovic.demo.data";

// Keep track of the message id so that we can remove it.
var gMessageIds = [];

function addBannerMessages() {
  // 140 characters
  let text = "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed nec purus in ante pretium blandit. Aliquam erat volutpat. Nulla libero lectus.";
  gMessageIds.push(Home.banner.add({
    text: text,
    icon: "data:image/png;base64,",
    onshown: function() {
      log("test message shown");
    },
    onclick: function() {
      log("test message clicked");
    },
    ondismiss: function() {
      log("test message dismissed");
    }
  }));

  gMessageIds.push(Home.banner.add({
    text: "32x32 data URI",
    icon: "data:image/png;base64,"
  }));
}

function removeBannerMessages() {
  gMessageIds.forEach(id => Home.banner.remove(id));
}

function savePanelData() {
  Task.spawn(function saveTask() {
    let storage = HomeProvider.getStorage(DATASET_ID);
    yield storage.deleteAll();
    yield storage.save([{
      "url": "http://mozilla.org",
      "title": "Mozilla",
      "description": "This is an example"
    }, {
      "url": "http://margaretleibovic.com",
      "title": "Margaret Leibovic",
      "description": "This is a second example"
    }, {
      "url": "http://nytimes.com",
      "title": "New York Times",
      "description": "Yet another example"
    }]);
  }).then(function(result) {
    log("saveTask: result - " + JSON.stringify(result));
  }, function(error) {
    log("saveTask: error - " + JSON.stringify(error));    
  });
}

function deletePanelData() {
  Task.spawn(function deleteTask() {
    let storage = HomeProvider.getStorage(DATASET_ID);
    yield storage.deleteAll();
  }).then(function(result) {
    log("deleteTask: result - " + JSON.stringify(result));
  }, function(error) {
    log("deleteTask: error - " + JSON.stringify(error));    
  });
}

/**
 * bootstrap.js API
 */

function startup(aData, aReason) {
  addBannerMessages();

  function listOptionsCallback() {
    return {
      title: "List Demo",
      layout: Home.panels.Layout.FRAME,
      views: [{
        type: Home.panels.View.LIST,
        dataset: DATASET_ID,
        emptyState: {
          text: "Larry says there's no data here",
          imageUrl: "drawable://larry"
        }
      }]
    };
  }

  function gridOptionsCallback() {
    return {
      title: "Grid Demo",
      layout: Home.panels.Layout.FRAME,
      views: [{
        type: Home.panels.View.GRID,
        dataset: DATASET_ID
      }]
    };
  }

  Home.panels.register(LIST_PANEL_ID, listOptionsCallback);
  Home.panels.register(GRID_PANEL_ID, gridOptionsCallback);

  switch(aReason) {
    case ADDON_INSTALL:
      //savePanelData();
      Home.panels.install(LIST_PANEL_ID);
      Home.panels.install(GRID_PANEL_ID);
      break;

    case ADDON_UPGRADE:
    case ADDON_DOWNGRADE:
      Home.panels.update(LIST_PANEL_ID);
      Home.panels.update(GRID_PANEL_ID);
      break;
  }
}

function shutdown(aData, aReason) {
  if (aReason == ADDON_UNINSTALL || aReason == ADDON_DISABLE) {
    deletePanelData();

    Home.panels.uninstall(LIST_PANEL_ID);
    Home.panels.uninstall(GRID_PANEL_ID);
  }

  Home.panels.unregister(LIST_PANEL_ID);
  Home.panels.unregister(GRID_PANEL_ID);

  removeBannerMessages();
}

function install(aData, aReason) {
}

function uninstall(aData, aReason) {
}
